package com.bns.Database;import android.util.*;public class Error {	static final int CREATEDB = 1;	static final int CREATETABLE = 2;	static final int INSERT = 3;	static final int DELETE = 4;	static final int UPDATE = 5;		public static void ErrorEvent(int errorType)	{		switch(errorType)		{		case 1:			Log.d("DB", "CREATE DB ERROR");			break;		case 2:			Log.d("Table", "CREATE TABLE ERROR");			break;		case 3:			Log.d("Insert", "INSERT DATA ERROR");			break;		case 4:			Log.d("Delete", "DELETE DATA ERROR");			break;		case 5:			Log.d("Update", "UPDATE DATA ERROR");			break;		default:			Log.d("Default", "Default");			break;		}	}}